mapper.select:
/* adjournmentMapper."adjournmentList" */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, adjinfo.adjournment_seq, adjinfo.member_seq, adjinfo.store_seq, adjinfo.order_seq, adjinfo.adjournment_start, adjinfo.adjournment_end, adjinfo.adjournment_state, adjinfo.adjournment_memo, adjinfo.del_yn, adjinfo.register_date, adjinfo.register_id, adjinfo.modify_date, adjinfo.modify_id, mbinfo.name, mbinfo.phone, stinfo.store_name, prdinfo.product_name FROM adjournmentinfo adjinfo INNER JOIN orderinfo ordinfo ON adjinfo.order_seq = ordinfo.order_seq INNER JOIN memberinfo mbinfo ON adjinfo.member_seq = mbinfo.member_seq INNER JOIN storeinfo stinfo ON adjinfo.store_seq = stinfo.store_seq INNER JOIN productinfo prdinfo ON ordinfo.product_seq = prdinfo.product_seq INNER JOIN (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND adjinfo.store_seq = #{storeSeq}
AND adjinfo.del_yn = 'N'
AND ( name LIKE CONCAT('%',#{searchWord},'%') OR phone LIKE CONCAT('%',#{searchWord},'%') OR product_name LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY adjinfo.modify_date DESC

/* adjournmentMapper."adjournmentTotalCount" */ SELECT COUNT(*) FROM adjournmentinfo adjinfo INNER JOIN orderinfo ordinfo ON adjinfo.order_seq = ordinfo.order_seq INNER JOIN memberinfo mbinfo ON adjinfo.member_seq = mbinfo.member_seq INNER JOIN storeinfo stinfo ON adjinfo.store_seq = stinfo.store_seq INNER JOIN productinfo prdinfo ON ordinfo.product_seq = prdinfo.product_seq INNER JOIN (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND adjinfo.store_seq = #{storeSeq}
AND adjinfo.del_yn = 'N'
AND ( name LIKE CONCAT('%',#{searchWord},'%') OR phone LIKE CONCAT('%',#{searchWord},'%') OR product_name LIKE CONCAT('%',#{searchWord},'%') )

mapper.update:
/* adjournmentMapper.updateAdjournmentState */
UPDATE adjournmentinfo
SET adjournment_state = #{adjournmentState},
	modify_date = now(),
	modify_id = #{modifyId}
WHERE adjournment_seq = #{adjournmentSeq}

/* adjournmentMapper.updateAdjournment */
UPDATE adjournmentinfo
SET	adjournment_start = #{adjournmentStart},
	adjournment_end = #{adjournmentEnd},
 	adjournment_state = #{adjournmentState},
	adjournment_memo = #{adjournmentMemo},
	modify_date =  now(),
	modify_id = #{modifyId}
WHERE adjournment_seq = #{adjournmentSeq}

