mapper.select:
/* itemMapper.itemList */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, tempA.store_seq, tempA.store_name, tempA.large_category, tempA.item_seq, tempA.item_name, tempA.del_yn, tempA.register_date, tempA.register_id, tempA.modify_date, tempA.modify_id FROM ( SELECT prditem.item_seq, prditem.store_seq, ( SELECT store_name FROM storeinfo WHERE store_seq = prditem.store_seq AND del_yn ='N') AS store_name, prditem.large_category, prditem.item_name, prditem.del_yn, prditem.register_date, prditem.register_id, prditem.modify_date, prditem.modify_id FROM productitem prditem WHERE 1=1
AND prditem.store_seq = #{storeSeq}
AND prditem.del_yn ='N' ORDER BY prditem.modify_date ASC ) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC

/* itemMapper.itemTotalCount */ SELECT COUNT(*) FROM ( SELECT prditem.item_seq, prditem.store_seq, ( SELECT store_name FROM storeinfo WHERE store_seq = prditem.store_seq AND del_yn ='N') AS store_name, prditem.large_category, prditem.item_name, prditem.del_yn, prditem.register_date, prditem.register_id, prditem.modify_date, prditem.modify_id FROM productitem prditem WHERE 1=1
AND prditem.store_seq = #{storeSeq}
AND prditem.del_yn ='N' ) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') )

mapper.update:
/* itemMapper.updateProductCode */
UPDATE productitem
SET product_code = #{productCode}
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.itemDelete */
UPDATE productitem
SET del_yn = 'Y'
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.updateItem */ UPDATE productitem
item_name = #{itemName}, large_category = #{largeCategory}, modify_date= now(), modify_id = 'admin',
WHERE item_seq = #{itemSeq} AND del_yn ='N'

mapper.select:
/* itemMapper.itemExcelDownload */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, tempA.store_seq, tempA.store_name, tempA.large_category, tempA.item_seq, tempA.item_name FROM ( SELECT prditem.item_seq, prditem.store_seq, ( SELECT store_name FROM storeinfo WHERE store_seq = prditem.store_seq AND del_yn ='N') AS store_name, prditem.large_category, prditem.item_name FROM productitem prditem WHERE 1=1
AND prditem.store_seq = #{storeSeq}
AND prditem.del_yn ='N' ORDER BY prditem.modify_date ASC ) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC
