mapper.select:
/* itemMapper.itemList */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, tempA.store_seq, tempA.store_name, tempA.large_category, tempA.item_seq, tempA.item_name, tempA.del_yn, tempA.register_date, tempA.register_id, tempA.modify_date, tempA.modify_id FROM ( SELECT item_seq, store_seq, ( SELECT store_name FROM STOREINFO WHERE store_seq = #{storeSeq} AND del_yn ='N') AS store_name, large_category, item_name, del_yn, register_date, register_id, modify_date, modify_id FROM PRODUCTITEM WHERE store_seq= #{storeSeq} AND del_yn ='N' ORDER BY modify_date ASC ) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC

mapper.update:
/* itemMapper.updateProductCode */
UPDATE PRODUCTITEM
SET product_code = #{productCode}
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.itemDelete */
UPDATE PRODUCTITEM
SET del_yn = 'Y'
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.updateItem */ UPDATE PRODUCTITEM
item_name = #{itemName}, large_category = #{largeCategory}, modify_date= now(), modify_id = 'admin',
WHERE item_seq = #{itemSeq} AND del_yn ='N'

mapper.select:
/* itemMapper.itemExcelDownload */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, tempA.store_seq, tempA.store_name, tempA.large_category, tempA.item_seq, tempA.item_name FROM ( SELECT item_seq, store_seq, ( SELECT store_name FROM STOREINFO WHERE store_seq = #{storeSeq} AND del_yn ='N') AS store_name, large_category, item_name FROM PRODUCTITEM WHERE store_seq= #{storeSeq} AND del_yn ='N' ORDER BY modify_date ASC ) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC
