mapper.select:
/* orderMapper.orderList */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, tempA.order_seq, tempA.store_seq, tempA.product_seq, tempA.member_seq, tempA.start_day, tempA.expiration_day, tempA.expiration_yn, tempA.locker_seq, tempA.del_yn, tempA.store_name, tempA.product_name, tempA.item_name, tempA.name, tempA.phone FROM ( SELECT ordinfo.order_seq, ordinfo.store_seq, ordinfo.product_seq, ordinfo.member_seq, ordinfo.start_day, ordinfo.expiration_day, ordinfo.expiration_yn, ordinfo.locker_seq, ordinfo.del_yn, ( SELECT store_name FROM STOREINFO WHERE store_Seq = #{storeSeq}) AS store_name, prdinfo.product_name, ( SELECT item_name FROM PRODUCTITEM WHERE item_seq = prdinfo.item_seq) As item_name, mbrinfo.name, mbrinfo.phone FROM ORDERINFO ordinfo INNER JOIN MEMBERINFO mbrinfo ON ordinfo.member_seq = mbrinfo.member_seq INNER JOIN PRODUCTINFO prdinfo ON ordinfo.product_seq = prdinfo.product_Seq WHERE ordinfo.store_seq = #{storeSeq} AND ordinfo.del_yn = 'N' ORDER By ordinfo.order_seq DESC) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.product_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.name LIKE CONCAT('%',#{searchWord},'%') OR tempA.phone LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(tempA.start_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(tempA.expiration_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC

mapper.update:
 		/* orderMapper.updateOrderCount */
		UPDATE ORDERINFO
		SET remaining_count = #{remainingCount}
		WHERE order_seq = #{orderSeq}
		AND del_yn = 'N'

 		/* orderMapper.orderDelete */
		UPDATE ORDERINFO
		SET del_yn = 'Y'
		WHERE order_seq = #{orderSeq}
		AND store_seq = #{storeSeq}
		AND del_yn = 'N'

/* orderMapper.updateOrder */ UPDATE ORDERINFO
product_seq = ${productSeq}, start_day = #{startDay}, expiration_day = #{expirationDay}, product_price = #{productPrice}, customer_price = #{customerPrice}, product_count = #{productCount}, remaining_count = #{remainingCount}, order_memo = #{orderMemo}, locker_seq = #{lockerSeq}, modify_date = now(), modify_id = 'admin'
WHERE order_seq = #{orderSeq} AND del_yn = 'N'

/* orderMapper.updateAdjournmentState */
UPDATE ORDERINFO

	adjournment_state = #{adjournmentState},
	modify_date = now(),
	modify_id = #{modifyId}

WHERE order_seq = #{orderSeq}

mapper.select:
/* orderMapper.orderExcelDown */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, tempA.order_seq, tempA.store_seq, tempA.product_seq, tempA.member_seq, tempA.start_day, tempA.expiration_day, tempA.expiration_yn, tempA.store_name, tempA.product_name, tempA.item_name, tempA.name, tempA.phone FROM ( SELECT ordinfo.order_seq, ordinfo.store_seq, ordinfo.product_seq, ordinfo.member_seq, ordinfo.start_day, ordinfo.expiration_day, ordinfo.expiration_yn, ordinfo.del_yn, ( SELECT store_name FROM STOREINFO WHERE store_Seq = #{storeSeq}) AS store_name, prdinfo.product_name, ( SELECT item_name FROM PRODUCTITEM WHERE item_seq = prdinfo.item_seq) As item_name, mbrinfo.name, mbrinfo.phone FROM ORDERINFO ordinfo INNER JOIN MEMBERINFO mbrinfo ON ordinfo.member_seq = mbrinfo.member_seq INNER JOIN PRODUCTINFO prdinfo ON ordinfo.product_seq = prdinfo.product_Seq WHERE ordinfo.store_seq = #{storeSeq} AND ordinfo.del_yn = 'N' ORDER By ordinfo.order_seq DESC) AS tempA , (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ( tempA.store_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.item_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.product_name LIKE CONCAT('%',#{searchWord},'%') OR tempA.name LIKE CONCAT('%',#{searchWord},'%') OR tempA.phone LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(tempA.start_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(tempA.expiration_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC
