mapper.select:
/* orderMapper.orderList */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, tempA.order_seq, tempA.store_seq, tempA.product_seq, tempA.customer_price, tempA.member_seq, tempA.my_membership, tempA.start_day, tempA.expiration_day, tempA.expiration_yn, tempA.locker_seq, tempA.del_yn, tempA.store_name, tempA.product_name, tempA.name, tempA.phone, tempA.register_date FROM ( SELECT ordinfo.order_seq, ordinfo.store_seq, ordinfo.product_seq, ordinfo.customer_price, ordinfo.member_seq, mbinfo.my_membership, ordinfo.start_day, ordinfo.expiration_day, ordinfo.expiration_yn, ordinfo.locker_seq, ordinfo.del_yn, stinfo.store_name, prdinfo.product_name, mbinfo.name, mbinfo.phone, ordinfo.register_date FROM orderinfo ordinfo INNER JOIN memberinfo mbinfo ON ordinfo.member_seq = mbinfo.member_seq INNER JOIN productinfo prdinfo ON ordinfo.product_seq = prdinfo.product_Seq INNER JOIN storeinfo stinfo ON ordinfo.store_seq = stinfo.store_seq INNER JOIN (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ordinfo.store_seq = #{storeSeq}
AND ordinfo.del_yn = 'N'
AND ( stinfo.store_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.my_membership LIKE CONCAT('%',#{searchWord},'%') OR ordinfo.customer_price LIKE CONCAT('%',#{searchWord},'%') OR prdinfo.product_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.phone LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.start_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.expiration_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY ordinfo.modify_date ASC ) AS tempA ORDER BY row_num DESC

/* orderMapper.orderTotalCount */ SELECT COUNT(*) FROM ( SELECT ordinfo.order_seq, ordinfo.store_seq, ordinfo.product_seq, ordinfo.customer_price, ordinfo.member_seq, mbinfo.my_membership, ordinfo.start_day, ordinfo.expiration_day, ordinfo.expiration_yn, ordinfo.locker_seq, ordinfo.del_yn, stinfo.store_name, prdinfo.product_name, mbinfo.name, mbinfo.phone, ordinfo.register_date FROM orderinfo ordinfo INNER JOIN memberinfo mbinfo ON ordinfo.member_seq = mbinfo.member_seq INNER JOIN productinfo prdinfo ON ordinfo.product_seq = prdinfo.product_seq INNER JOIN storeinfo stinfo ON ordinfo.store_seq = stinfo.store_seq WHERE 1=1
AND ordinfo.store_seq = #{storeSeq}
AND ordinfo.del_yn = 'N'
AND ( stinfo.store_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.my_membership LIKE CONCAT('%',#{searchWord},'%') OR ordinfo.customer_price LIKE CONCAT('%',#{searchWord},'%') OR prdinfo.product_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.phone LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.start_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.expiration_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') )
) AS tempA

mapper.update:
 		/* orderMapper.updateOrderCount */
		UPDATE orderinfo
		SET remaining_count = #{remainingCount}
		WHERE order_seq = #{orderSeq}
		AND del_yn = 'N'

 		/* orderMapper.orderDelete */
		UPDATE orderinfo
		SET del_yn = 'Y'
		WHERE order_seq = #{orderSeq}
		AND store_seq = #{storeSeq}
		AND del_yn = 'N'

/* orderMapper.updateOrder */ UPDATE orderinfo
product_seq = ${productSeq}, start_day = #{startDay}, expiration_day = #{expirationDay}, product_price = #{productPrice}, customer_price = #{customerPrice}, product_count = #{productCount}, remaining_count = #{remainingCount}, order_memo = #{orderMemo}, locker_seq = #{lockerSeq}, modify_date = now(), modify_id = 'admin'
WHERE order_seq = #{orderSeq} AND del_yn = 'N'

/* orderMapper.updateAdjournmentState */
UPDATE orderinfo

	adjournment_state = #{adjournmentState},
	modify_date = now(),
	modify_id = #{modifyId}

WHERE order_seq = #{orderSeq}

mapper.select:
/* orderMapper.orderExcelDown */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, tempA.order_seq, tempA.store_seq, tempA.product_seq, tempA.customer_price, tempA.member_seq, tempA.my_membership, tempA.start_day, tempA.expiration_day, tempA.expiration_yn, tempA.locker_seq, tempA.del_yn, tempA.store_name, tempA.product_name, tempA.name, tempA.phone, tempA.register_date FROM ( SELECT ordinfo.order_seq, ordinfo.store_seq, ordinfo.product_seq, ordinfo.customer_price, ordinfo.member_seq, mbinfo.my_membership, ordinfo.start_day, ordinfo.expiration_day, ordinfo.expiration_yn, ordinfo.locker_seq, ordinfo.del_yn, stinfo.store_name, prdinfo.product_name, mbinfo.name, mbinfo.phone, ordinfo.register_date FROM orderinfo ordinfo INNER JOIN memberinfo mbinfo ON ordinfo.member_seq = mbinfo.member_seq INNER JOIN productinfo prdinfo ON ordinfo.product_seq = prdinfo.product_Seq INNER JOIN storeinfo stinfo ON ordinfo.store_seq = stinfo.store_seq INNER JOIN (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND ordinfo.store_seq = #{storeSeq}
AND ordinfo.del_yn = 'N'
AND ( stinfo.store_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.my_membership LIKE CONCAT('%',#{searchWord},'%') OR ordinfo.customer_price LIKE CONCAT('%',#{searchWord},'%') OR prdinfo.product_name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.name LIKE CONCAT('%',#{searchWord},'%') OR mbinfo.phone LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.start_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') OR DATE_FORMAT(ordinfo.expiration_day, '%Y-%m-%d %H:%i') LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY ordinfo.modify_date ASC ) AS tempA ORDER BY row_num DESC
