mapper.select:
/* productMapper.productList */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, prdinfo.product_seq, prdinfo.store_seq, prdinfo.product_name, prdinfo.product_price, prdinfo.del_yn, prdinfo.register_date, prdinfo.register_id, prdinfo.modify_date, prdinfo.modify_id, stinfo.store_name, prdinfo.item_seq, prditem.item_name FROM PRODUCTINFO prdinfo INNER JOIN (SELECT @ROWNUM :=0) AS R INNER JOIN STOREINFO stinfo ON prdinfo.store_seq = stinfo.store_seq INNER JOIN PRODUCTITEM prditem ON prdinfo.item_seq = prditem.item_seq WHERE 1=1
AND prdinfo.store_seq = #{storeSeq}
AND prdinfo.del_yn = 'N'
AND ( store_name LIKE CONCAT('%',#{searchWord},'%') OR item_name LIKE CONCAT('%',#{searchWord},'%') OR product_name LIKE CONCAT('%',#{searchWord},'%') OR product_price LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC

mapper.update:
/* productMapper.productDelete */
UPDATE PRODUCTINFO
SET del_yn ='Y'
WHERE product_seq = #{productSeq}
AND store_seq = #{storeSeq}
AND del_yn = 'N'

/* productMapper.updateProduct */ UPDATE PRODUCTINFO
item_seq =#{itemSeq}, product_name = #{productName}, product_price = #{productPrice}, locker_seq = #{lockerSeq}, product_memo = #{productMemo}, modify_date = now(), modify_id = 'admin'
WHERE product_seq = #{productSeq} AND store_seq = #{storeSeq} AND del_yn ='N'

mapper.select:
/* productMapper.productExcelDown */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, prdinfo.product_seq, prdinfo.store_seq, prdinfo.product_name, prdinfo.product_price, stinfo.store_name, prdinfo.item_seq, prditem.item_name FROM PRODUCTINFO prdinfo INNER JOIN (SELECT @ROWNUM :=0) AS R INNER JOIN STOREINFO stinfo ON prdinfo.store_seq = stinfo.store_seq INNER JOIN PRODUCTITEM prditem ON prdinfo.item_seq = prditem.item_seq WHERE 1=1
AND prdinfo.store_seq = #{storeSeq}
AND prdinfo.del_yn = 'N'
AND ( store_name LIKE CONCAT('%',#{searchWord},'%') OR item_name LIKE CONCAT('%',#{searchWord},'%') OR product_name LIKE CONCAT('%',#{searchWord},'%') OR product_price LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY row_num DESC
