mapper.update:
/* itemMapper.updateProductCode */
UPDATE PRODUCTITEM
SET product_code = #{productCode}
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.itemDelete */
UPDATE PRODUCTITEM
SET del_yn = 'Y'
WHERE item_seq = #{itemSeq}
AND del_yn ='N'

/* itemMapper.updateItem */ UPDATE PRODUCTITEM
item_name = #{itemName}, large_category = #{largeCategory}, modify_date= now(), modify_id = 'admin',
WHERE item_seq = #{itemSeq} AND del_yn ='N'
