mapper.update:
/* manageMapper.updateFailureCount */
UPDATE MANAGEINFO
SET failure_count = failure_count +1
WHERE id = #{id}

mapper.select:
/* manageMapper.checkFailureCount */
SELECT	failure_count
FROM MANAGEINFO
WHERE id = #{id}

mapper.update:
/* manageMapper.updateEnabled */
UPDATE MANAGEINFO
SET enabled = false
WHERE id = #{id}

/* manageMapper.updateFailureCountReset */
UPDATE MANAGEINFO
SET failure_count = 0
WHERE id = #{id}

/* manageMapper.updateAccessDate */
UPDATE MANAGEINFO
SET access_date = now(),
	modify_date = now(),
	modify_id = #{id}
WHERE id = #{id}

mapper.select:
/* manageMapper.manageList */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, mginfo.manage_seq, mginfo.id, mginfo.passwd, mginfo.name, mginfo.authority, CASE WHEN mginfo.authority = 'ROLE_ADMIN' THEN '관리자' WHEN mginfo.authority = 'ROLE_MANAGER' THEN '매장관리자' WHEN mginfo.authority = 'ROLE_STAFF' THEN '직원' ELSE '기타' END AS authority_name, mginfo.access_date, mginfo.enabled, mginfo.store_seq, ( SELECT store_name FROM STOREINFO WHERE store_seq = mginfo.store_seq ) AS store_name, mginfo.use_yn FROM MANAGEINFO mginfo INNER JOIN (SELECT @ROWNUM :=0) AS R WHERE 1=1
AND mginfo.store_seq = #{storeSeq} AND mginfo.authority NOT IN ('ROLE_ADMIN')
ORDER BY mginfo.store_seq DESC;

mapper.update:
 		/* manageMapper.updateEnableState */
		UPDATE MANAGEINFO
		SET enabled = #{enabled},
			modify_date = now(),
			modify_id = #{id}
		WHERE manage_seq = #{manageSeq}
