mapper.select:
/* memberMapper.memberList */ SELECT @ROWNUM:=@ROWNUM+1 as row_num, member_seq, name, sex, phone, email, birth, memo, store_seq, register_date, adjournment_state FROM MEMBERINFO , (SELECT @ROWNUM :=0) AS R WHERE store_seq = #{storeSeq} AND del_yn = 'N'
AND ( name LIKE CONCAT('%',#{searchWord},'%') OR birth LIKE CONCAT('%',#{searchWord},'%') OR phone LIKE CONCAT('%',#{searchWord},'%') OR email LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY register_date DESC, row_num DESC

mapper.update:
/* memberMapper.memberDel */
UPDATE MEMBERINFO 
SET del_yn = 'Y'
WHERE member_seq = #{memberSeq}
AND store_seq = #{storeSeq}

/* memberMapper.memberEdit */ UPDATE MEMBERINFO
name = #{name}, sex = #{sex}, birth = #{birth}, phone = #{phone}, email = #{email}, memo = #{memo}, modify_date= now(), modify_id = 'admin'
WHERE member_seq = #{memberSeq} AND store_seq = #{storeSeq} AND del_yn ='N'
