mapper.select:
/* storeMapper.storeList */ SELECT @ROWNUM:=@ROWNUM+1 AS row_num, store_seq, store_name, store_tel, store_phone, store_address, store_memo FROM STOREINFO, (SELECT @ROWNUM :=0) AS R WHERE del_yn = 'N'
AND ( store_name LIKE CONCAT('%',#{searchWord},'%') OR store_tel LIKE CONCAT('%',#{searchWord},'%') OR store_phone LIKE CONCAT('%',#{searchWord},'%') OR store_address LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY store_seq DESC

mapper.update:
/* storeMapper.storeDelete */
UPDATE STOREINFO

	del_yn = 'Y'

WHERE store_seq = #{storeSeq}
AND del_yn = 'N'

/* storeMapper.updateStore */ UPDATE STOREINFO
store_name = #{storeName}, store_tel = #{storeTel}, store_phone = #{storePhone}, store_address = #{storeAddress}, store_memo = #{storeMemo}, modify_date =now(), modify_id = 'admin'
WHERE store_seq = #{storeSeq} AND del_yn = 'N'

